package Lec2;

import java.util.Scanner;

public class Odd_even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		int n =s.nextInt();
		if(n%2==0) {
			System.out.println("Even no");
		}
		else {
			System.out.println("Odd no");
		}

	}

}
